import './App.css'
import React, { PureComponent } from 'react'
import CryptoBanner from './banner'

class App extends PureComponent {

  constructor (props) {
    super(props)
    this.state = {
      tokens: [],
      tokensLoaded: false
    }
  }

  renderForm () {
    const { tokens } = this.state

    return (
      <div className={'form'}>
        <span>Grab your domain today:</span>
        <div className={'input'}>
          <input type={'text'} className={'domain-string'} />
          <select className={'token-tld'}>
            {tokens.map((token, i) => {
              return (
                <option key={`option-${i}`} value={token}>
                  {token}
                </option>
              )
            })}
          </select>
        </div>
      </div>
    )
  }

  render () {
    const { tokensLoaded } = this.state

    return (
      <div className={'App'}>
        <div class={'header'}>
          <CryptoBanner />
          <h1>ERC-20 Name Service Registrar</h1>
        </div>
        {
          tokensLoaded
          ? this.renderForm()
          : null
        }
      </div>
    )   
  }
}

export default App
