  renderForm () {
    const { domainName, domainTld, tokens } = this.state

    return (
      <div className={'form'}>
        <span>Your domain, your tokens. Grab yours today:</span>
        <div className={'input'}>
          <input onChange={this.onDomainChange.bind(this)} type={'text'} className={'domain-string'} />
          <div className={'period'}>
            <span>.</span>
          </div>
          <select className={'token-tld'} onChange={this.onTldChange.bind(this)}>
            {tokens.map((token, i) => {
              return (
                <option key={`option-${i}`} value={token}>
                  {token}
                </option>
              )
            })}
          </select>
        </div>
        {
          domainName.length
          ? <div className={'preview'}>
              <span>{`${domainName}.${domainTld}`}</span>
            </div>
          : null
        }
      </div>
    )
  }

  render () {
    const { tokensLoaded } = this.state

    return (
      <div className={'App'}>
        <div className={'header'}>
          <CryptoBanner />
          <h1>ERC-20 Name Service Registrar</h1>
        </div>
        {
          tokensLoaded
          ? this.renderForm()
          : null
        }
      </div>
    )   
  }
}

export default App
